    public void Sintactico(String token)
    {
        boolean ban = true;
        int col = buscarColumna(token);
        int ren = Integer.parseInt(pila.peek().substring(1));
        String dato = tabla[ren][col];
        String datoComp;
        
        if(!dato.isEmpty()) {
            datoComp = dato.startsWith("I") ? dato.substring(1) : dato;
            while(!datoComp.matches("^[1-9][0-9]*$")) {
                if(datoComp.isEmpty()) {
                    errSint = true;
                    ban = false;
                    break;
                }
                
                String num = datoComp.substring(1);
                String[] prod = producciones[Integer.parseInt(num)].split("#");
                String[] simb = prod[1].trim().split(" ");
                
                if(!simb[0].equals("vacia")) {
                    for(int i = 0; i < simb.length * 2; i++)
                        pila.pop();
                }
                
                String cima = pila.peek().substring(1);
                String nuevo = tabla[Integer.parseInt(cima)][buscarColumna(prod[0].trim())];
                pila.push(prod[0]);
                pila.push(nuevo);
                pilaAux();
                
                if(prod[0].equals("VAR")) {
                    switch(simb[0]) {
                        case "id":
                            pilaSem.push(buscarTipo(datoVar));
                            break;
                        default:
                            pilaSem.push(Tipo());
                    }                                
                }
                if(prod[0].equals("R")) {
                    t2 = pilaSem.pop();
                    t1 = pilaSem.pop();
                    
                    if(!comp[t1][t2]) {
                        error = "No se puede comparar un valor de tipo " + buscarID(t1) + " con un valor de tipo " + buscarID(t2) + ".";
                        ban = false;
                        break;
                    }
                }
                
                if(prod[0].equals("F")) {
                    switch(simb[0]) {
                        case "id":
                            pilaSem.push(buscarTipo(datoVar));
                            break;
                        case "num", "litcar", "litcad":
                            pilaSem.push(Tipo());
                    }
                }
                if(prod[0].equals("E") || prod[0].equals("T")) {
                    if(simb.length > 1) {
                        t2 = pilaSem.pop();
                        t1 = pilaSem.pop();

                        if(exp[t1][t2] == -1){
                            error = "La operación de " + Operacion(simb[1]) + " entre " + buscarID(t1) + " y " + buscarID(t2) + " no puede realizarse";
                            ban = false;
                            break;
                        } else
                            pilaSem.push(exp[t1][t2]);
                    }
                }
                
                ren = Integer.parseInt(pila.peek().substring(1));
                col = buscarColumna(token);
                dato = tabla[ren][col];
                datoComp = dato.startsWith("I") ? dato.substring(1) : dato;
                
                if(dato.equals("p0")) {
                    ban = false;
                    break;
                }
            }
            if(ban) {                
                if(pila.peek().equals("I26")) {
                    t1 = buscarTipo(pilaID.peek());
                    t2 = Tipo();
                    if(!reglaAsig[t1][t2]) {
                        error = "La variable " + pilaID.peek() + " de tipo " + buscarID(t1) + " no puede recibir un " + buscarID(t2) + ".";
                        return;
                    }
                }
                if((pila.peek().equals("I46") || pila.peek().equals("I56") || pila.peek().equals("I121")) && token.equals("id")) {                   
                    varAsig = buscarTipo(datoVar);
                    variable = datoVar;
                    
                    // Método
                    if(varAsig == 4) {
                        
                    }
                }
                if(pila.peek().equals("I66") && token.equals(";")) {
                    if(!pilaSem.isEmpty()) {
                        if(!reglaAsig[varAsig][pilaSem.peek()]) {
                            error = "La variable " + variable + " de tipo " + buscarID(varAsig) + " no puede recibir un " + buscarID(pilaSem.peek()) + ".";
                            pilaSem.pop();
                            return;
                        } else {
                            pilaSem.pop();
                        }
                    }
                }                
                
                pila.push(token);
                pila.push(dato);
                pilaAux();
            }
        } else {
            errSint = true;
        }
    }
    
    private void pilaAux()
    {
        StringBuilder cadena = new StringBuilder();
        for(String elemento : pila) {
            cadena.append(elemento);
        }
        pilaAux.push(cadena.toString());
    }
    
    private int buscarColumna(String token)
    {
        int col = -1;
        for(int i = 0; i < columnas.length; i++)
            if(token.equals(columnas[i])) {
                col = i;
                break;
            }
        return col;
    }
    
    public void Reinicio()
    {
        var = "";
        datoVar = "";
        varAsig = -1;
        variable = "";
        error = "";
        t1 = -1;
        t2 = -1;
        pilaAux.clear();
        pila.clear();
        pila.push("$");
        pila.push("I0");
        pilaAux.push("$I0");
        errSint = false;
        temp.clear();
        pilaSem.clear();
        tablaSim = new ArrayList<>();
        pilaID.clear();
    }
    
    public String Esperado()
    {
        int ren = Integer.parseInt(pila.peek().substring(1));
        String msj = "";
        for(int i = 0; i < tabla[ren].length; i++)
            if(!tabla[ren][i].isEmpty() && !Arrays.asList(not).contains(columnas[i]))
                msj += columnas[i] + ", ";
        
        return msj.substring(0, msj.length() - 2);
    }
    
    public Stack<String> Pila()
    {
        return pilaAux;
    }
    
    private int Tipo()
    {
        String entero = "^[+-]?\\d+$";
        
        switch(var) {           
            case "num":
                if(datoVar.matches(entero))
                    return 0;
                else
                    return 1;
            case "litcar":
                return 2;
            case "litcad":
                return 3;
        }
        return -1;
    }
    
    private int buscarTipo(String lexema)
    {
        for(String[] vars: tablaSim)
            if(vars[0].equals(lexema)) {
                if(!vars[1].equals("m"))
                    return Integer.parseInt(vars[1]);
                else
                    return 4;
            }
        return -1;
    }
    
    private String buscarID(int tipo)
    {
        switch(tipo) {
            case 0 : 
                return "int"; 
            case 1:
                return "float";
            case 2:
                return "char";
            case 3:
                return "String";
        }
        return "";
    }
    
    private String Operacion(String op)
    {
        switch(op) {
            case "+":
                return "suma";
            case "-":
                return "resta";
            case "*":
                return "multiplicación";
            case "/":
                return "división";
        }
        return "";
    }
    
    public void Variable(String token, String lexema)
    {
        var = token;
        datoVar = lexema;
    }
    
    public void Tabla(List<String[]> tablaSim)
    {
        this.tablaSim = tablaSim;
    }
    
    public void ID(String id)
    {
        pilaID.push(id);
    }
