    private Stack<String> pila;
    private Stack<String> pilaAux;

    public Sintactico() {
        pila = new Stack();
        pilaAux = new Stack();
        pila.push("$");
        pila.push("I0");
        pilaAux.push("z");
        resultado = "$ I0 \n";
        error = "";
        codigoInt = "";
    }

    public void BuscarElemento(String lexema, int linea) {
        //Busca el lexema en los terminales y no terminales para encontrar su índice
        for (indTerm = 0; indTerm < tnt.length; indTerm++) {
            if (tnt[indTerm].equals(lexema)) {
                //Revisa el elemento que se encuentra en la tabla con el índice encontrado del lexema y
                //el último estado que se encuentra en la pila sea una cadena vacía, lo que indica un
                //estado de error
                if (tabla[Integer.parseInt(pila.peek().split("I")[1])][indTerm].equals("")) {
                    error += "Error sintáctico en la línea " + linea + " esperaba "
                            + Error(Integer.parseInt(pila.peek().split("I")[1])) + "\n";
                    return;
                }
                //Este método revisa si el elemento de la tabla comparado anteriormente es
                //un estado al empezar con la letra I, en dicho caso se realiza un desplazamiento
                //en caso contrario es una Producción y se realiza una reducción
                else if (tabla[Integer.parseInt(pila.peek()
                        .split("I")[1])][indTerm].startsWith("I")) {
                    //Desplazamiento
                }
                else{
                    //Reduccion
                }
            }
        }
    }
    
    private void Desplazamiento(String lexema, String estado, int linea){
        //Se agregan el lexema y el estado al 
        pila.push(lexema);
        pila.push(estado);
        //Aquí va todo el switch del semántico
    }
    
    private void Reduccion(String lexema, int produccion, int linea){
        int estado;
        //Aquí iría el switch del código intermedio
        
        while(!producciones[produccion].split("#")[1].equals("vacia")){
            if(pila.pop().equals(producciones[produccion].split("#")[1].split(" ")[0])){
                break;
            }
        }
        for(indProd = 0; indProd < tnt.length; indProd++)
        {
            if(tnt[indProd].equals(producciones[produccion].split("#")[0])){
                estado = Integer.parseInt(pila.peek().split("I")[1]);
                
                // Aquí iría una sección para agregar el fin de if en el código intermedio
                
                
                if(tabla[estado][indProd].equals("")){
                    return;
                }
                pila.push(producciones[produccion].split("#")[0]);
                pila.push(tabla[estado][indProd]);
                AñadirResultado();
                BuscarElemento(lexema, linea);
            }
        }
        if(producciones[produccion].equals(producciones[0])){
            resultado += producciones[0] + " Acepta la cadena";
            //Aquí iría la parte del código intermedio donde se cierra el programa
        }
    }
    
    //Este método sirve para añadir lo que tiene la pila a 
    //una variable resultado
    private void AñadirResultado(){
        while(!pila.peek().equals("$")){
            pilaAux.push(pila.pop());
        }
        resultado += "$";
        while(!pilaAux.peek().equals("z")){
            resultado += " " + pila.push(pilaAux.pop());
        }
        resultado += "\n";
    }

    //Este método retorna una cadena con los terminales que esperaba
    //Ejemplo: "Esperaba: int, char, variable"
    private String Error(int I) {
        int i, it;
        String simEsperados = "";
        //El 32 corresponde a los valores del arreglo de terminales
        //y no terminales que son únicamente terminales, esto va a cambiar en tu tabla final
        for (i = 0; i <= 32; i++) {
            simEsperados += tnt[i] + ", ";
        }
        return simEsperados;
    }
